import React, { useState } from "react";

function AddTodo() {
  const [task, setTask] = useState("");
  const [description, setDescription] = useState("");

  const handleAdd = (e) => {
    e.preventDefault();
    console.log({ task, description }); // later connect to Flask API
    setTask("");
    setDescription("");
  };

  return (
    <div className="add-todo-page mx-auto mt-5 shadow p-4 bg-white rounded">
      <h3 className="text-center mb-4 text-primary">Add a New Todo</h3>
      <form onSubmit={handleAdd}>
        <div className="mb-3">
          <label className="form-label fw-semibold">Task</label>
          <input
            type="text"
            className="form-control"
            placeholder="Enter task title"
            value={task}
            onChange={(e) => setTask(e.target.value)}
          />
        </div>

        <div className="mb-3">
          <label className="form-label fw-semibold">Description</label>
          <textarea
            className="form-control"
            rows="3"
            placeholder="Enter task description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          ></textarea>
        </div>

        <div className="text-center">
          <button className="btn btn-primary px-4">Add Todo</button>
        </div>
      </form>
    </div>
  );
}

export default AddTodo;
