import { useFormik } from "formik";
import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { todoSchema } from "../schema";
import { APiRequest } from "../services/ApiRequest";
import { toast } from "react-toastify";

const initialValues = {
  task: "",
  description: "",
};

function AddTodo() {
  const { id } = useParams();

  const [isEdit, setIsEdit] = useState(false);
  const [loading, setLoading] = useState(false);

  const navigate = useNavigate();

  const formik = useFormik({
    initialValues,
    validationSchema: todoSchema, 
    enableReinitialize: true, 
    onSubmit: async (values, action) => {
      try {
            setLoading(true);
        if (isEdit) {
          await APiRequest.updateTodo(id, values);
          alert("Todo Updated Successfully!");
        } else {
          const res = await APiRequest.addTodo(values);
          console.log("Response::::::", res);
          alert("Todo Added Successfully!");
        }

        action.resetForm();
        navigate("/");

      } catch (error) {
        console.error("Error in form submission:", error);
        alert("Something went wrong!");
      }
    },
  });

  const { values, errors, touched, handleBlur, handleChange, handleSubmit, setValues } = formik;

  useEffect(() => {
    const fetchTodo = async () => {
      if (id) {
        try {
          const res = await APiRequest.getSingleTodo(id);
          const todo = res.data;

          setValues({
            task: todo.task || "",
            description: todo.description || "",
          });
        } catch (error) {
          console.error("Error fetching todo:", error);
          alert(" Failed to load todo data!");
        }
      }
    };

    fetchTodo();
  }, [id, setValues]);

  return (
    <div className="add-todo">
      <div className="card">
        <h2 className="card__title">
          {isEdit ? "Edit Todo" : "Add a New Todo"}
        </h2>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Task</label>
            <input
              type="text"
              name="task"
              value={values.task}
              onChange={handleChange}
              onBlur={handleBlur}
              placeholder="Enter task title"
            />
            {errors.task && touched.task ? (
              <p className="form-error">{errors.task}</p>
            ) : null}
          </div>

          <div className="form-group">
            <label>Description</label>
            <textarea
              rows="3"
              name="description"
              value={values.description}
              onChange={handleChange}
              onBlur={handleBlur}
              placeholder="Enter task description"
            ></textarea>
            {errors.description && touched.description ? (
              <p className="form-error">{errors.description}</p>
            ) : null}
          </div>

          <button type="submit" className="btn">
            {isEdit ? "Update Todo" : "Add Todo"}
          </button>
        </form>
      </div>
    </div>
  );
}

export default AddTodo;
