import React, { useState } from "react";
import { FaEdit, FaTrashAlt } from "react-icons/fa";
import "."

function App() {
  const [tasks, setTasks] = useState([]);
  const [input, setInput] = useState("");

  const handleAdd = () => {
    if (input.trim() !== "") {
      setTasks([...tasks, { text: input, completed: false }]);
      setInput("");
    }
  };

  return (
    <div className="todo-container d-flex flex-column align-items-center justify-content-start py-5">
      <div className="todo-card shadow-lg p-4 rounded">
        <h2 className="text-center mb-4">üìù To-Do App</h2>

        {/* Input Area */}
        <div className="input-group mb-3">
          <input
            type="text"
            className="form-control"
            placeholder="Enter your task..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
          />
          <button className="btn btn-primary" onClick={handleAdd}>
            Add Task
          </button>
        </div>

        {/* Task List */}
        <ul className="list-group">
          {tasks.length === 0 && (
            <li className="list-group-item text-center text-muted">
              No tasks yet ‚Äî add one above!
            </li>
          )}
          {tasks.map((task, index) => (
            <li
              key={index}
              className={`list-group-item d-flex justify-content-between align-items-center ${
                task.completed ? "completed" : ""
              }`}
            >
              <div>
                <input
                  type="checkbox"
                  className="form-check-input me-2"
                  checked={task.completed}
                  onChange={() => {
                    const newTasks = [...tasks];
                    newTasks[index].completed = !newTasks[index].completed;
                    setTasks(newTasks);
                  }}
                />
                <span>{task.text}</span>
              </div>
              <div>
                <button className="btn btn-sm btn-outline-success me-2">
                  <FaEdit />
                </button>
                <button
                  className="btn btn-sm btn-outline-danger"
                  onClick={() => {
                    setTasks(tasks.filter((_, i) => i !== index));
                  }}
                >
                  <FaTrashAlt />
                </button>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
